{
  "query": "mutation CreateAccount($input: CreateAccountInput!) { createAccount(input: $input) { id ownerName branch number currentBalance status } }",
  "variables": {
    "input": {
      "ownerName": "Alice",
      "document": "52998224725",
      "branch": "0001",
      "number": "12345-1",
      "initialBalance": "1000.00"
    }
  }
}

{
  "query": "mutation CreateAccount($input: CreateAccountInput!) { createAccount(input: $input) { id ownerName branch number currentBalance status } }",
  "variables": {
    "input": {
      "ownerName": "Bob",
      "document": "39053344705",
      "branch": "0001",
      "number": "12345-2",
      "initialBalance": "500.00"
    }
  }
}

{
  "query": "mutation Transfer($input: TransferFundsInput!) { transferFunds(input: $input) { idempotentReplay fromAccountBalance toAccountBalance transaction { id fromAccountId toAccountId amount description createdAt } } }",
  "variables": {
    "input": {
      "fromAccountId": "{{fromAccountId}}",
      "toAccountId": "{{toAccountId}}",
      "amount": "120.00",
      "description": "Sample transfer",
      "idempotencyKey": "{{idempotencyKey}}"
    }
  }
}

{
  "query": "query Accounts($first: Int!, $after: String, $status: AccountStatus) { accounts(first: $first, after: $after, status: $status) { edges { cursor node { id ownerName currentBalance status } } pageInfo { hasNextPage endCursor } } }",
  "variables": {
    "first": 10,
    "after": null,
    "status": "ACTIVE"
  }
}

{
  "query": "query Transactions($accountId: ID!, $direction: TransactionDirection!, $first: Int!, $after: String) { transactionsByAccount(accountId: $accountId, direction: $direction, first: $first, after: $after) { edges { cursor node { id fromAccountId toAccountId amount description createdAt } } pageInfo { hasNextPage endCursor } } }",
  "variables": {
    "accountId": "{{fromAccountId}}",
    "direction": "SENT",
    "first": 10,
    "after": null
  }
}
